import React, { Component } from "react";

import "./Calculator.css";

import CalculatorConsts from "../common/consts/calculator.consts";
import Button from "../components/Button/Button";
import Display from "../components/Display/Display";

const initialState = {
  displayValue: "0",
  clearDisplay: false,
  operation: null,
  values: [0, 0],
  current: 0,
};
export default class Calculator extends Component {
  state = { ...initialState };

  clear() {
    console.log("clear");

    this.setState({ initialState });
  }

  setDigit(n) {
    const clearDisplay =
      this.state.displayValue === "0" || this.state.clearDisplay;
    console.log("set digit");
    if (n === "." && !clearDisplay && this.state.displayValue.includes("."))
      return;

    const currentValue = clearDisplay ? "" : this.state.displayValue;
    const displayValue = currentValue + n;
    this.setState({ displayValue, clearDisplay: false });

    if (n !== ".") {
      const newValue = parseFloat(displayValue);
      const values = [...this.state.values];
      values[this.state.current] = newValue;
      this.setState({ values });
    }
  }

  setOperation(operation) {
    console.log("set op");
    if (this.state.current === 0) {
      this.setState({ operation, current: 1, clearDisplay: true });
    } else {
      const equals = operation === "=";
      const values = [...this.state.values];
      let displayHistory = "";
      try {
        displayHistory = `${values[0]} ${this.state.operation} ${values[1]}`;
        values[0] = eval(`${values[0]} ${this.state.operation} ${values[1]}`);
      } catch (e) {
        values[0] = this.state.values[0];
      }
      values[1] = 0;
      this.setState({
        displayValue: `${values[0]}`,
        displayHistory,
        operation: equals ? null : operation,
        current: equals ? 0 : 1,
        clearDisplay: true,
        values,
      });
    }
  }

  setMethod(itemClass, label) {
    const botao = label.trim();
    const classe = itemClass.trim();
    if (botao === "AC") return this.clear;
    else if (classe === "operation" || classe === "col4")
      return this.setOperation;
    else return this.setDigit;
  }
  render() {
    const { buttons } = CalculatorConsts();

    const RenderButtons = () =>
      buttons.map((item, index) => {
        console.log(item.class, item.label);
        let click = this.setMethod(item.class, item.label);
        return (
          <Button
            key={index}
            label={item.label}
            style={item.class}
            onClick={() => click}
          />
        );
      });

    return (

        {/* <RenderButtons /> */}
    );
  }
}
